// this line should not be removed
var WReadTweets = angular.module("WReadTweets",[]);

WReadTweets.controller( "WReadTweetsController", function( $scope ) {
    $scope.getFeeds = function(arg) { 
     var dq = new DataQuery( 'qReadTweets' );
     dq.setParameters({"screen_name":$scope.screen_name});
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData();
		});
	});
   };

});
