// this line should not be removed
var wCurrencyExchange = angular.module( "Wcurrency2",[]);
wCurrencyExchange.controller( "Wcurrency2Controller", function( $scope ) {
    var dq = new DataQuery( 'qGetCurrs' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.results = dq.getData();
    		});
    	});

    $scope.writecurrency = function() { 
        console.log($scope.convertFrom);
        var dq = new DataQuery( 'qGetCountryWithCurrency' );
        dq.setParameters({"countrycode":$scope.convertFrom});
        dq.execute().
        	done( function() {
        		$scope.$apply(function() {
        			$scope.retData = dq.getData();
        			$scope.fromrate =$scope.retData.cur;
        			console.log($scope.fromrate);
        		});
        	});
    };

    $scope.writecurrency1 = function() { 
        console.log($scope.convertTo);
        var dq = new DataQuery( 'qGetCountryWithCurrency' );
        dq.setParameters({"countrycode":$scope.convertTo});
        dq.execute().
        	done( function() {
        		$scope.$apply(function() {
        			$scope.retData1 = dq.getData();
                    $scope.torate=$scope.retData1.cur;
                    console.log($scope.torate);
    		    });
    	    });
    };

    $scope.ConvertCurrency = function(arg)  {
       rr=  $scope.amount/$scope.fromrate;
       $scope.resultofconversion= rr*$scope.torate;
       console.log($scope.resultofconversion);
    };
});
