var sidewidgetTree = angular.module("sidewidgetTree",[]);

sidewidgetTree.controller( "sidewidgetTreeController", function( $scope ) {

    // this line should not be removed
        var wTree = angular.module("wTree",[]);

            $scope.output = ''; 
            $scope.newBranch = '' 
            $scope.my_tree = {}; 
            $scope.my_tree_handler = function(branch) { 
                var _ref; 
                $scope.output = "You selected: " + branch.label; 
                if ((_ref = branch.data) != null ? _ref.description : void 0) { 
                    return $scope.output += '(' + branch.data.description + ')'; 
                } 
            }; 

            $scope.addBranch = function() { 
                var b; 

                b = $scope.my_tree.get_selected_branch(); 
                return $scope.my_tree.add_branch(b, { 
                    label: $scope.newBranch, 
                    data: { 
                        something: 42, 
                        "else": 43 
                    } 
                }); 
            }; 

            apple_selected = function(branch) { 
                return $scope.output = "APPLE : " + branch.label; 
            }; 

