// this line should not be removed
var w5WeatherAPI = angular.module("w5WeatherAPI",[]);

w5WeatherAPI.controller( "w5WeatherAPIController", function( $scope ) {
    $.get("http://ipinfo.io", function(response) {
        var dq = new DataQuery( 'q5DayWeather' );
        dq.setParameters({"city":response.city});
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData().data;
			console.log($scope.results)
		});
	});
    $scope.cit=response.city;
    $scope.count=response.country;


    console.log(response.city, response.country);
}, "jsonp");

});
