// this line should not be removed
var wAccord = angular.module("wAccord",[]);

wAccord.controller( "wAccordController", function( $scope ) {
	var dq = new DataQuery( 'qAccord' );
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.accordion_data = dq.getData();
		});
	});

});
