// this line should not be removed
var wGridImages = angular.module( "wAccordionDynamicClass",[]);

wGridImages.controller( "wAccordionDynamicClassController", function( $scope ) {
    var dq = new DataQuery( 'qTasksAccordion' );
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.tasks = dq.getData();
		});
	});
	$scope.showPriority = function(arg)  {
        $.SmartMessageBox({
            title : "This is a <b>"+arg+"</b>",
            content : "This is a "+arg+" Priority task",
            buttons : '[Ok]'
        }, function(ButtonPressed) {
            alert("Priority : "+arg);
        });
};


});
