// this line should not be removed
var wAlertDirective = angular.module( "wAlertDirectiveAutoHide",[]);

wAlertDirective.controller( "wAlertDirectiveAutoHideController", function( $scope ) {
  $scope.man=false;
    $scope.alerts = [{
		message: "Well done! You successfully read this important alert message.",
		type: "success"
	},{
		message: "Heads up! This alert needs your attention, but it's not super important.",
		type: "info"
	},{
		message: "Warning! Best check yo self, you're not looking too good.",
		type: "warning"
	},{
		message: "Oh snap! Change a few things up and try submitting again.",
		type: "danger"
	}];
    $scope.close = function(index){		
		console.log(index, $scope.alerts);
		$scope.alerts.splice(index, 1);
	}
	$scope.addAlert = function(){		
		$scope.alerts.push({"message":$scope.textme,"type":$scope.alerttype});
	};

});

wAlertDirective.directive("alert", function(){
	return{
		restrict: 'EA',
		template:'<div class="alert alert-{{type}} alert-dismissible fade in">'+
'	<button type="button" class="close" data-dismiss="alert" aria-hidden="true" ng-click=close()>×</button>'+
'<span><strong>{{message}}</strong></span>	'+
'</div>',
		replace: true,
		scope:{
			message: "@message",
			type: "@type",
			close: "&"

		},
		link: function(scope,elems,attrs){
		}

	};
});
