// this line should not be removed
var wModernLayout = angular.module( "wArchitectureSlides",[]);

wModernLayout.controller( "wArchitectureSlidesController", function( $scope ) {


    //---------------------- Beginning of use case data section --------------------------
    $scope.slides = [
        "/resources/Examples/archImages/DFX1.jpg",
        "/resources/Examples/archImages/DFX2.jpg",
        "/resources/Examples/archImages/DFX3.jpg",
        "/resources/Examples/archImages/DFX4.jpg",
        "/resources/Examples/archImages/DFX5.jpg"
    ];


    //---------------------- End of use case data section --------------------------

    //---------------------- This code is executed the first time the widget is created --------------------------
    //Display first item in the array
    $scope.currentIndex = 0
    $scope.selSlide = $scope.slides[$scope.currentIndex];




    //-------------- Change the index of the "card" to be shown" and apply it to the "model structure" used by the GC
    $scope.navigate = function(arg) {

        $scope.currentIndex = $scope.currentIndex + arg;

        if ($scope.currentIndex < 0) {
            $scope.currentIndex = $scope.slides.length - 1;
        } else if ($scope.currentIndex >= $scope.slides.length) {
            $scope.currentIndex = 0 ;
        }

        $scope.selSlide = $scope.slides[$scope.currentIndex]; 

        return '';
    };    

    $scope.goHome = function() {
        loadURL("Home.html");
    };



});

