// this line should not be removed
var wBPMApproveJobReq = angular.module( "wBPMApproveJobReq",[]);

wBPMApproveJobReq.controller( "wBPMApproveJobReqController", function( $scope ) {

	$scope.actionDone = '';

    //Get Task Details
    var dq = new DataQuery( 'qBPMGetTaskDetails' );
    //$scope.taskId = '945';
    $scope.taskId = sessionStorage.taskId;
    dq.setParameters( {"task_id": $scope.taskId} );
    dq.execute().
        done( function() {
            $scope.$apply(function() {
                $scope.task_details = dq.getData().data.data.variables;
            });
        });


    $scope.submitTask = function(arg) {

        var dq_submit = new DataQuery( 'qBPMApproveReq' );
        dq_submit.setParameters( {"task_id": $scope.taskId, "action":"finish", "decision": arg} );
        dq_submit.execute().
            done( function() {
                    $scope.$apply(function() {
                        $scope.actionDone = 'y';
                    });
               loadURL ("sDashboard.html");
            });
    return '';
    };	


});
