// this line should not be removed
var wGetBPMTaskList = angular.module( "wBPMGetTasksList",[]);

wGetBPMTaskList.controller( "wBPMGetTasksListController", function( $scope ) {

    var dq = new DataQuery( 'qBPMGetTasksMinified' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.results = dq.getData();
    		});
    	});


    $scope.dispatchAlert = function(arg) {

        var taskSubject = arg.rowdata.taskSubject;

        switch (taskSubject){
            case 'GM Approval Requisition':{
                sessionStorage.taskId = arg.rowdata.taskId;
                loadURL('sBPMScreen2.html');
                break;
            }
            case 'Step: Select candidate for interview':{
                sessionStorage.taskId = arg.rowdata.taskId;
                loadURL('sBPMScreen3.html');
                break;
            }
        }
        return '';
    };



});
