// this line should not be removed
var wBPMApproveJobReq = angular.module( "wBPMSelectCandidateOK",[]);

wBPMApproveJobReq.controller( "wBPMSelectCandidateOKController", function( $scope ) {

	//Initialization
    //$scope.selectedCandidate = null;


    //Get Task Details
    var dq = new DataQuery( 'qBPMGetTaskDetails' );
    $scope.taskId = '71';
    //$scope.taskId = sessionStorage.taskId;
    dq.setParameters( {"task_id": $scope.taskId} );
    dq.execute().
        done( function() {
            $scope.$apply(function() {
                $scope.results = dq.getData().data;
                $scope.task_details = $scope.results.data.variables;
                $scope.piid = $scope.results.piid;
                console.log($scope.task_details);

            var dq2 = new DataQuery( 'qBPMGetListCandidates' );
            dq2.execute().
            	done( function() {
            		$scope.$apply(function() {
            			$scope.candidates = dq2.getData().data;
            			$scope.candidateInfo = $scope.candidates[0];
            		});
            	});    
            });
        });

    $scope.flipCarousel=function (currentSlide){
        $scope.$apply(function() {
        $scope.candidateInfo=$scope.candidates[currentSlide.carouselIndex];	});
    };


    $scope.selectCandidate = function(arg) {

        var dq_sel_candidate = new DataQuery( 'qBPMSelectCandidate' );
        dq_sel_candidate.setParameters(
            {   "task_id": $scope.taskId,
                "action": "finish",
                "decision": "",
                "lastName": $scope.selectedCandidate.lastName,
                "firstName": $scope.selectedCandidate.firstName,
                "supervisor": $scope.selectedCandidate.supervisor,
                "payLevel": $scope.selectedCandidate.payLevel,
                "payType": "full time",
                "hrcandidateAvailable": true
            }
        );
        dq_sel_candidate.execute().
            done( function() {
                loadURL('sBPMTasks.html');
            });
    };

    //function that call twitter then watson and returns analytics from watson
    $scope.analyzeTwitterWatson = function() { 

        //first query returns concatinated response from twitter
        var dq = new DataQuery( 'qTwitterConcat' );
        dq.setParameters({"screen_name":$scope.selectedCandidate.screen_name});
        dq.execute().
        	done( function() {
        		$scope.$apply(function() {
        			var twits = dq.getData();
        			//removing special characters to make it a valid input for watson
        			var tw=twits[0].replace(/[^a-zA-Z ]/g, "");
        			//console.log(tw.toString());
        			//defining request body of second watson query
                    var second_body={"contentItems": [
                        {
                            "id": "xxx",
                            "userid": "abc",
                            "sourceid": "twitter",
                            "contenttype": "text/plain",
                            "language": "en",
                            "content":tw
                        }
                    ]};
                    //we are inside the first query and we will call the second watson query
        			var dq1 = new DataQuery( 'qWatsonUserModeling' );
        			dq1.setReqBody(second_body);
                    dq1.execute().
                    	done( function() {
                    		$scope.$apply(function() {
                    			$scope.moods = dq1.getData().tree.children;
                    			//console.log($scope.moods);
                    		});
                    	});

        		});
        	});

        };

    $scope.selectForReview = function() { 
        //$scope.$apply(function() {
                $scope.selectedCandidate = $scope.candidateInfo;
        //});
        //$scope.analyzeTwitterWatson ($scope.selectedCandidate.screen_name);
    };







});
