var wBPMstartJobRequisition = angular.module( "wBPMStartJobReq",[]);

wBPMstartJobRequisition.controller( "wBPMStartJobReqController", function( $scope ) {

    //---------------------------------------------------- Parameters and Data setting -----------
    //Hard code the locations to populate the combobox
    $scope.locations=[
    {"name": "Arkansas","abbreviation": "AR"},{"name": "California","abbreviation": "CA"},{"name": "Colorado","abbreviation": "CO"},{"name": "Connecticut","abbreviation": "CT"},
    {"name": "Delaware","abbreviation": "DE"},{"name": "District Of Columbia","abbreviation": "DC"},{"name": "Florida","abbreviation": "FL"},{"name": "Georgia","abbreviation": "GA"},
    {"name": "Massachusetts","abbreviation": "MA"},{"name": "Michigan","abbreviation": "MI"},{"name": "New Jersey","abbreviation": "NJ"},{"name": "New York","abbreviation": "NY"},
    {"name": "North Carolina","abbreviation": "NC"},{"name": "Ohio","abbreviation": "OH"},{"name": "Pennsylvania","abbreviation": "PA"},{"name": "Rhode Island","abbreviation": "RI"},
    {"name": "Texas","abbreviation": "TX"},{"name": "Virginia","abbreviation": "VA"},{"name": "Washington","abbreviation": "WA"}
    ];
    //Poplate other combobox with these choices
    $scope.emp_stat=[{"label":"Full Time"},{"label":"Part Time"},{"label":"Contractor"}];
    $scope.dep_opt=[{"label":"Software Engineering"},{"label":"Human Resources"},{"label":"Finance"},{"label":"Marketing"},{"label":"Product Development"},{"label":"Sales"},{"label":"Customer Service"},{"label":"Business Partner Service"},{"label":"Business Strategies"}];
    $scope.pos_type=[{"label":"New"},{"label":"Existing"}];
    $scope.existing_jobs = [{"job":"Sandy Monroe - VP Development"}, {"job":"Lisa Sams - VP Marketing"},{"job":"Mike Halo - Accountant"},{"job":"Amy Liu - Developer"}];

    $scope.request_mdl = Math.floor((Math.random() * 10000));
    $scope.hiring_manager_mdl = "Tom Ford";
    $scope.employment_mdl = "Full Time";
    $scope.department_mdl = "Marketing";
    $scope.location_mdl = "New York";
    $scope.position_mdl = "Existing";
    $scope.job_mdl = "VP of Marketing";
    $scope.reqCreated = ''; //will be set once the Req has been created 
    $scope.request_mdl_str=  $scope.request_mdl.toString();
    //---------------------------------------------------- End of Parameters and Data setting -----------



    //Call the BPM engine to start a new job requisition process, get the assigned piid from the BPM engine
	$scope.submitJobReq = function(arg) { 
	    var dq_sjr = new DataQuery( 'qBPMStartBPD' );
	    //console.log ("date" + $scope.date_mdl );
	    var paramsVar= {"currentPosition":{"positionType":$scope.position_mdl,"jobTitle":$scope.job_mdl,"iId":"","replacement":{"lastName":"","firstName":"","supervisor":"","payLevel":"","payType":"","notes":""}},"requisition":{"reqNum":$scope.request_mdl_str,"requestor": $scope.hiring_manager_mdl,"status":$scope.employment_mdl,"approvalNeeded":"false","date":"2014-11-24T22:11:47Z","department":$scope.department_mdl,"location":$scope.location_mdl,"empNum":"1","gmApproval":"","gmComments":"","instanceId":"","hrcandidateAvailable":"false"}};	   
	    var paramVarStr =JSON.stringify(paramsVar);
	    dq_sjr.setParameters( {"params":paramVarStr});
        dq_sjr.execute().
        	done( function() {
	        	$scope.$apply(function() {
	    		$scope.results = dq_sjr.getData().data;
	    		console.log($scope.results);
	    		$scope.reqCreated = $scope.results.piid + '/' + $scope.request_mdl;
	    		$scope.alertSuccess();
		    });
	    });
	};

    $scope.alertSuccess = function () {

        var alertMessage = "A new job requisiton with reference " + $scope.reqCreated + " has been created";
        if ($scope.position_mdl == "Existing") {
            alertMessage = alertMessage + " replacing " + $scope.replacement_mdl ;
        }

        $.smallBox({
            title: "Job Requisition submitted",
            content: "<i class='fa fa-clock-o'></i> <i>" + alertMessage + "</i>",
            color: "#296191",
            iconSmall: "fa fa-thumbs-up bounce animated",
            timeout: 6000
        });
    };

    // call this function when you click on the button "Close"
    $scope.gotoHome = function() { 
        loadURL("sDashboard.html");
        return '';
    };

});

