// this line should not be removed
var wModernLayout2 = angular.module( "wBPMTasks",[]);

wModernLayout2.controller( "wBPMTasksController", function( $scope ) {


    $scope.dummyMessages = [
        "Lorem ipsum dolor sit amet, numquam posidonium ex mea, mel ei wisi commune scriptorem. Sit doming propriae id, mel tation tantas cu, nobis clita possit nam an.",
        "Qui id odio dicit instructior, mel te graeci adipiscing, ei sapientem honestatis has. Ex semper inimicus his, te vix enim assum vituperatoribus.Quo soluta aliquam ei, vim accumsan.",
        "Ad duo mutat fugit mediocrem. An epicurei imperdiet honestatis cum. Audiam dissentias usu ad. His ut amet veniam scribentur.Iriure appareat cu sea, eam ad sonet evertitur...",
        "vim audire volutpat cu. Vero paulo regione at quo. Mel mutat perfecto eu, at sint omnis nam. Debet omnes ius ut, iusto consul percipit est ut. Vis ei modus meliore qualisque"
        ];


    //Call these functions when Widget is created
    $scope.currMsgIndex = 0;
    $scope.msgToDisplay = $scope.dummyMessages[$scope.currMsgIndex];

    //add some cool animation to the home icon
    addAnimation('.myClass1', 'pulse');

    //----------------- get all BPM Tasks: bpmTasks { taskId, taskSummary, taskPriority, taskSubject, taskSmallIcon, taskLargeIcon, processId}
    var dq = new DataQuery( 'qBPMGetTasksMinified' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.bpmTasks = dq.getData();
    		});
    	});
    //---------------------- End of init code --------------------------

    //---------------- Call the appropriate screen based on the task being clicked
    $scope.followOnTask = function(index) {

        var taskSubject = $scope.bpmTasks[index].taskSubject;

        switch (taskSubject){
            case 'GM Approval Requisition':{
                sessionStorage.taskId = $scope.bpmTasks[index].taskId;
                loadURL('sBPMScreen2.html');
                break;
            }
            case 'Step: Select candidate for interview':{
                sessionStorage.taskId = $scope.bpmTasks[index].taskId;
                loadURL('sBPMScreen3.html');
                break;
            }
        }
        return '';
    };
    //--------------- end function


    //---------------- go to Home Page
    $scope.goHome = function() {
        loadURL('Home.html');
        return '';
    };
    //--------------- end function


    //-------------- Change the index of the "card" to be shown" and apply it to the "model structure" used by the GC
    $scope.navigate = function(arg) {

        $scope.currMsgIndex = $scope.currMsgIndex + arg;

        if ($scope.currMsgIndex < 0) {
            $scope.currMsgIndex = $scope.dummyMessages.length - 1;
        } else if ($scope.currMsgIndex >= $scope.dummyMessages.length) {
            $scope.currMsgIndex = 0 ;
        }

        $scope.msgToDisplay = $scope.dummyMessages[$scope.currMsgIndex]; 

        return '';
    }; 


});

//add Animation to the object holding the class name defined in the parameter className. These objects have already the class "animated" defined for them. animationStyle can be a vast variety of options
// consult http://daneden.github.io/animate.css/ to see all the animation styles available
addAnimation = function(className, animationStyle) {
    $(className).mouseenter( function() {
        $(className).addClass(animationStyle);
    });

    $(className).mouseout( function() {
        $(className).removeClass(animationStyle);
    });

    return '';
};

