// this line should not be removed
/* Samples Gallery wBadgesAndLabels Widget, this demonstrates how to use Bootsrap Bades and labels classes on static text GCs. */
var wBadgesAndLabels = angular.module("wBadgesAndLabels",[]);

wBadgesAndLabels.controller( "wBadgesAndLabelsController", function( $scope ) {
    //Defining Grid Data with Class names.
	$scope.badges=[
        {"class":"badge","name":"Default"},
        {"class":"badge alert-primary","name":"Primary"},
        {"class":"badge alert-success","name":"Success"},
        {"class":"badge alert-info","name":"Info"},
        {"class":"badge alert-warning","name":"Warning"},
        {"class":"badge alert-danger","name":"Danger"}
    ];
    $scope.labels=[
        {"class":"label label-default","name":"Default"},
        {"class":"label label-primary","name":"Primary"},
        {"class":"label label-success","name":"Success"},
        {"class":"label label-info","name":"Info"},
        {"class":"label label-warning","name":"Warning"},
        {"class":"label label-danger","name":"Danger"}
    ];
});
