// this line should not be removed
var wEmpCarousel = angular.module( "wCNNCarousel",[]);

wEmpCarousel.controller( "wCNNCarouselController", function( $scope ) {

    $scope.currentRow = 0;

	var dq = new DataQuery( 'qCnnRss' );
	dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData();
		});
	});

$scope.pass=function (dsds){
    console.log(dsds.carouselIndex);
    $scope.$apply(function() {
    $scope.info=$scope.results[dsds.carouselIndex];	});
};
});
