// this line should not be removed
var wChartSub = angular.module("wChartSub",[]);

wChartSub.controller( 'wChartSubController', ['$scope', 'messageService', function ($scope, messageService) {
            $scope.sub_message = '';
            $scope.cand1=7;
            $scope.cand2=5;
            $scope.data = {
    "series": ["Candidate1", "Candidate2"],
    "data": [
        {
            "x": "Candidate1",
            "y": [
                $scope.cand1
            ],
            "tooltip": "this is tooltip"
        },
        {
            "x": "Candidate2",
            "y": [
                $scope.cand2

            ]
        }
    ]
}

            messageService.subscribe('notification', function(event, parameters) {
        $scope.sub_message = parameters.message;
        console.log($scope.sub_message);
        if($scope.sub_message=="1"){
            $scope.$apply(function() {
			 $scope.cand1++;
		});

            console.log($scope.cand1);
        }
         if($scope.sub_message=="2"){
            $scope.$apply(function() {
			 $scope.cand2++;
		});}
    });
}]);
