var wCheckBocToggle = angular.module("wCheckBocToggle",[]);

wCheckBocToggle.controller( "wCheckBocToggleController", function( $scope ) {
    $scope.hour="true";
    $scope.minute="true";
    $scope.second="true";
   	$scope.myDelay = function() {
            var d = new Date();
            $scope.$apply(function(){
                $scope.second_mdl = parseInt(d.getSeconds());
                $scope.minute_mdl = parseInt(d.getMinutes());
                $scope.hour_mdl = parseInt(d.getHours());})
                var t = setTimeout(function(){$scope.myDelay()},500);
        }
          $scope.myDelay();
          console.log($scope.hour,$scope.minute,$scope.second);
});
