var wRadioToggle = angular.module( "wCheckToggle",[]);

wRadioToggle.controller( "wCheckToggleController", function( $scope ) {
    $scope.show_vlaue = function(arg) {
        console.log($scope.decision); 
        if($scope.decision==false){
		$scope.$apply(function() {
		     $scope.decision=false;
		});}
		else($scope.$apply(function() {
		     $scope.decision=true;
		}));
    };
   });
