// this line should not be removed
var wTwitterOP = angular.module( "wCities",[]);

wTwitterOP.controller( 'wCitiesController', function( $scope ) {
    var dq = new DataQuery( 'qChooseCity' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.cities = dq.getData().cities;
    		});
    	});
});
