// this line should not be removed
var wClock = angular.module("wClock",[]);

wClock.controller( "wClockController", function( $scope ) {

    $scope.cSize=500;

    // (0) Setup the context to paint the clock based on the canvas object
    var clockCanvas = document.getElementById("myClock"); //get an Id on the clock HTML tag
    var ctx = clockCanvas.getContext("2d"); //get a drawing context to use the Canvas object
    var radius = (clockCanvas.height) / 2; //position the radius of the clock in the middle
    ctx.translate(radius, radius); //Move the reference (0,0) to become the center of the clock; all further drawings would use this center as (0,0)

    //--------------------- General Parameters
    //$scope.analogClock = 1;
    var brandName = "Marc Watches";
    var brandColor = "black";
    var brandFont = 0.05 * radius + "px Arial";
    var dateTagFont = 0.08 * radius + "px Arial";;
    var dateTagColor = "red"
    var monthShortNames= ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
    var today = new Date();
    var thisMonthAndDay = monthShortNames[today.getMonth()] + "  " + today.getDate();

    //---------------------- Global colors used
    // (1) Clock background
    var clockBack1 = "silver";
    var clockBack2 = "white";
    var nbrRingsClockBack = 2;

    // (2) Clock contour
    var clockControur1 = "grey";
    var clockContour2 = "white";
    var contourThickness = 0.15; //percent of radius, expressed as a number
    var contourBoldness = 0.1; //Line thickness to draw the contour, expressed as a percent of the radius
    radius = radius * (1- contourBoldness); //Reduce the radius by the boldness factor to keep everything fitting

    // (3) Clock handles
    var handleColor = "red";
    var handleHrWidth = parseInt(8 * radius / 100);
    var handleHrLen = radius * 0.5;
    var handleHrColor = "red";
    var handleMinWidth = parseInt(4 * radius / 100);
    var handleMinLen = radius * 0.7;
    var handleMinColor = "gray";
    var handleSecWidth = parseInt(radius / 100);
    var handleSecLen = radius * 0.85;
    var handleSecColor = "white";

    // (4) Clock central Knob
    var centralKnobPercent = 0.05; //Central "knob, expressed in terms of the radius
    var centralKnobColor = handleColor;

    // (5) Dials config
    var romanDials = ["I", "II", "III", "IV", "V", "VI", "VII", "VIII", "IX", "X", "XI", "XII"];
    var dialsFont = 0.1 * radius + "px Verdana";
    var dialsColor = "white";
    var romans = true;

    $scope.turnClockOn = function() {    
        setInterval($scope.paintClock, 1000);
    };

    $scope.paintClock = function() {
        $scope.paintBackground();

        if ($scope.analogClock == 1) {
            $scope.paintDials();
            $scope.paintRealTimeAnalog();
        } else {
            paintRealTimeDigital();
        }

    }

    $scope.paintBackground = function() {
        var colGrad;
        var i;

        //--------- (1) Paint the Clock background
        ctx.beginPath();
        ctx.arc(0, 0, radius, 0, 2*Math.PI);
        //Decorate the background with gradient colors
        colGrad = ctx.createRadialGradient(0,0,0, 0,0,radius);
        for (i=1; i<= nbrRingsClockBack; i++) {
            colGrad.addColorStop(i/nbrRingsClockBack, (i % 2 == 0) ? clockBack1 : clockBack2);
        }
        ctx.fillStyle = colGrad;
        ctx.fill();


        //--------- (2) Paint the Clock background
        colGrad = ctx.createRadialGradient(0,0,radius*(1-contourThickness), 0,0,radius*(1+contourThickness));
        //Give 3D effect
        colGrad.addColorStop(0, clockControur1);
        colGrad.addColorStop(0.5, clockContour2);
        colGrad.addColorStop(1, clockControur1);
        ctx.strokeStyle = colGrad;
        ctx.lineWidth = radius*contourBoldness;
        ctx.stroke();

        //--------- (3) Paint the brand
        $scope.paintBrand(0, -radius*0.55);

    }

    $scope.paintDials = function() {
        var ang;
        var num;

        //--------- (1) Paint the Clock central screw, it will be drawn in the color of the handles
        ctx.beginPath();
        ctx.arc(0, 0, radius*centralKnobPercent, 0, 2*Math.PI);
        ctx.fillStyle = centralKnobColor;
        ctx.fill();

        //--------- (2) Paint the Dials
        ctx.font = dialsFont;
        ctx.textBaseline="middle";
        ctx.fillStyle = dialsColor;
        ctx.textAlign="center";

        for(num = 1; num < 13; num++){
            ang = num * Math.PI / 6;
            ctx.rotate(ang);
            ctx.translate(0, -radius*0.85);
            ctx.rotate(-ang);

            if (romans) {
                ctx.fillText(romanDials[num-1], 0, 0);
            } else {
                ctx.fillText(num.toString(), 0, 0);
            }

            ctx.rotate(ang);
            ctx.translate(0, radius*0.85);
            ctx.rotate(-ang);
        }
        //--------- (3) Paint the Date
        $scope.paintDate(radius*0.5, 0);

    }

    $scope.paintBrand = function (middleX, middleY) {
        ctx.font=brandFont;
        ctx.textAlign="center";
        ctx.textBaseline = "middle";
        ctx.fillStyle = brandColor;
        ctx.fillText(brandName,middleX,middleY);
        return
    }

    $scope.paintDate = function(middleX, middleY) {
        ctx.font=dateTagFont;
        ctx.textAlign="center";
        ctx.textBaseline = "middle";
        ctx.fillStyle = dateTagColor;
        ctx.fillText(thisMonthAndDay,middleX, middleY);
        return
    }

    $scope.paintRealTimeAnalog = function() {
        var now = new Date();
        var hour = now.getHours();
        var minute = now.getMinutes();
        var second = now.getSeconds();

        // Get the hour
        //Get the Hour angle
        hour=(hour*Math.PI/6)+ (minute*Math.PI/(6*60))+ (second*Math.PI/(360*60));
        $scope.paintHandle(hour, handleHrLen, handleHrWidth, handleHrColor);

        //Get the minutes
        minute=(minute*Math.PI/30)+(second*Math.PI/(30*60));
        $scope.paintHandle(minute, handleMinLen, handleMinWidth, handleMinColor);
        // second
        second=(second*Math.PI/30);
        $scope.paintHandle(second, handleSecLen, handleSecWidth, handleSecColor);
    }

    $scope.paintHandle = function(ang, length, width, color) {
        ctx.beginPath();
        ctx.lineWidth = width;
        ctx.lineCap = "round";
        ctx.strokeStyle = color;
        ctx.moveTo(0,0);
        ctx.rotate(ang);
        ctx.lineTo(0, -length);
        ctx.stroke();
        ctx.rotate(-ang);
    }

    function paintRealTimeDigital() {
        var now = new Date();
        var fullTime;

        var hour = now.getHours();
        if (hour < 10) {
            hour = "0" + hour;
        }

        var minute = now.getMinutes();
        if (minute < 10) {
            minute = "0" + minute;
        }
        var second = now.getSeconds();
        if (second < 10) {
            second = "0" + second;
        }

        fullTime = hour + ":" + minute + ":" + second;
        ctx.font= radius*0.4 + "px Arial";
        ctx.textAlign="center";
        ctx.textBaseline = "middle";
        ctx.fillStyle = dateTagColor;
        ctx.fillText(fullTime,0, 0);

        //--------- (2) Paint the Date
        $scope.paintDate(0, radius*0.55);
    }

});

