var wRadioSurvey = angular.module( "wComboSurvey",[]);

wRadioSurvey.controller( "wComboSurveyController", function( $scope ) {
    $scope.questions=[{"label":"Select Your Programming Language","answer":"","type":"languages"},{"label":"Years Of Experience","answer":"","type":"years"}];
    $scope.answers={"languages":[{"label":"Java"},{"label":"python"}],"years":[{"label":"one"},{"label":"Two"}]};
    $scope.showHelp = function(arg) {
        $.SmartMessageBox({
            title : "ComboBoxes in Dynamic Panel",
            content : "We have a Dynamic Combobox,Text inside a Dynamic Panel. The Combobox property is bind to a json array that has the answers, and the text is binf to the question. A single Question with the Answers will be displayed in each panel dynamically. For more info about this example click <a href='#'>Here</a>",
            buttons : '[Ok]'
        });
};
});
