// this line should not be removed
var wcInbox = angular.module( "wCommonInbox",[]);

wcInbox.controller( "wCommonInboxController", function( $scope ) {

	//Initialize sessionStorage 
	sessionStorage.claimId = '';
	sessionStorage.subscriberId = '';

	//Get All alerts
	var dq = new DataQuery( 'qTaskComb' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.alerts = dq.getData();
    			var temp =dq.getData();
    			var emails=[];
    			var claims=[];
    			var tasks=[];

    			for(i=0;i<temp.length;i++){
    			    if (temp[i].alertType=="alertReadEmail")
    			     {emails.push(temp[i]);}
    			    if (temp[i].alertType=="alertViewClaim")
    			    {claims.push(temp[i]);}

    			    if (temp[i].alertType=="alertBPM")
    			    {tasks.push(temp[i]);}
    			}
    			$scope.emails=emails;
    			$scope.claims=claims;
    		    $scope.tasks=tasks;



    		});
    	});

    $scope.dispatchAlert = function(arg) {

        var alertType = arg.rowdata.alertType;

        switch (alertType){
            case 'alertViewClaim':{
                sessionStorage.subscriberId = arg.rowdata.alertId;
                loadURL('sClaimCenter.html');
                break;
            }
            case 'alertManageSubscriber':{
                sessionStorage.subscriberId = arg.rowdata.alertId;
                loadURL('sManageSubscribers.html');
                break;
            }
            case 'alertBPM':{
                sessionStorage.taskId = arg.rowdata.alertId;
                loadURL('sSelectCandidate.html');
                break;
            }
            case 'alertReadEmail':{
                break;
            }
        }
        return '';
    };

});
