// this line should not be removed
var wDFHomePage = angular.module( "wDFHomePage",[]);

wDFHomePage.controller( "wDFHomePageController", function( $scope ) {

        $scope.name = "John Doe";

        $scope.messages = [
        {
            title: "You are a winner!",
            content: "<i class='fa fa-thumbs-up'></i><b>   " + $scope.name + "</b>, you have made it<p>check the power of <a href='http://www.interactive-clouds.com' target='_blank'>DreamFace</a></p>",
            color: "#69c46a",
            icon: "fa fa-check fadeInRight animated",
            iconSmall: "fa fa-user fa-4x fadeInRight animated",
            timeout: 4000
        },
        {
            title: "Danger Zone!",
            content: "<i class='fa fa-fire fa=2x'></i><b>   " + $scope.name + "</b>, <i>you are about to fall in love with this product!</i>",
            color: "#C46A69",
            icon: "fa fa-times fadeInRight animated",
            iconSmall: "",
            timeout: 4000
        },    
        {
            title: "Warning",
            content: "<i class='fa fa-flag'></i><b>   you can keep this message longer by hovering the mouse on it</i>",
            color: "#c09853",
            icon: "fa fa-shield fadeInRight animated",
            iconSmall: "",
            timeout: 4000
        }
        ];

    $scope.learnMore = function () {
	    var newWindow = window.open("http://www.interactive-clouds.com", "_blank");
	};


    $scope.fancyAlert = function(smallOrBig, aMessage) { 
        if (smallOrBig =="S") {
            $.smallBox (aMessage);           
        } else {
            $.bigBox (aMessage);            
        }
    };
});
