// this line should not be removed
var wDirectives2 = angular.module("wDirectives2",[]);

wDirectives2.controller( "wDirectives2Controller", function( $scope ) {
	$scope.animations=[{"class":"flipInX"},{"class":"rotateIn"},{"class":"hinge"},{"class":"flipOutY"}];
});
wDirectives2.directive('enter', function() {
  return {
    restrict: 'C',
    link: function (scope, element,attrs) {
      element.bind('mouseenter',function(){
			element.addClass("animated " + scope.animation);
		})
    }
  };
});

wDirectives2.directive('leave', function() {
  return {
    restrict: 'C',
    link: function (scope, element,attrs) {
      element.bind('mouseleave',function(){
			element.removeClass("animated " + scope.animation);
		})
    }
  };
});
