// this line should not be removed
/* Samples Gallery wDynamicAccord Widget, in this widget we demonstrate how to display data from external API in dynamic accordion using different Graphical Controls. */
var wAccord = angular.module( "wDynamicAccord",[]);

wAccord.controller( "wDynamicAccordController", function( $scope ) {
    //Defining DataQuery
	var dq = new DataQuery( 'qUsBoxOfficeNew' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.accordion_data = dq.getData();
    		});
    	});

});
