var wDynamicTab = angular.module( "wDynamicTabFunctions",[]);

wDynamicTab.controller( "wDynamicTabFunctionsController", function( $scope ) {
    $scope.testify=function(item){
         $scope.something=item;
console.log(item.firstChild.getAttribute("href"))};
    $scope.gettabItems = function() {
        var dq = new DataQuery( 'qGetUsersTab' );
        dq.execute().
        	done( function() {
        		$scope.$apply(function() {
        			$scope.tab_data = dq.getData();
        		});
	});
};
$scope.gettabItems();

});
