// this line should not be removed
var wEmp = angular.module("wEmp",[]);

wEmp.controller( "wEmpController", function( $scope ) {
	 var dq = new DataQuery( 'qListEmp' );
	dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.employees = dq.getData().data;
		});
	});
});
