// this line should not be removed
var wEmpCarousel = angular.module("wEmpCarousel",[]);

wEmpCarousel.controller( "wEmpCarouselController", function( $scope ) {
	var dq = new DataQuery( 'qGetCarouselItems' );
	dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData().data;
		});
	});
$scope.pass=function (dsds){
    console.log(dsds.carouselIndex);
    $scope.$apply(function() {
    $scope.info=$scope.results[dsds.carouselIndex];	});
};
});
