// this line should not be removed
var wEmployeeList = angular.module("wEmployeeList",[]);

wEmployeeList.controller( "wEmployeeListController", function( $scope ) {
    var dq = new DataQuery( 'qListAllEmployees' );
	dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.employees = dq.getData().data;
		});
	});


});
