var example7Widget1 = angular.module( "wExample7Widget1",[]);

example7Widget1.controller( "wExample7Widget1Controller",['$scope',"messageService", function( $scope,messageService ) {
    var dq = new DataQuery( 'qGetCustomerEx7' );
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData().data;

		});
	});


$scope.searchOpt=[{"display":"List All Customers","value":"ListAll"},{"display":"Search by Customer Name","value":"CustomerName"},{"display":"Search by Contact Name","value":"ContactName"},{"display":"Search by Country","value":"Country"}];
//	console.log($scope.searchOpts);
$scope.myFunction = function(arg) { 
    console.log("hi");
    $scope.searchResults=[];
    var old_array=$scope.results.slice(0);
    console.log(old_array);
    console.log($scope.cst_name);
		var i = old_array.length;
		var new_array=[];

	if($scope.AttSearch=="ListAll"){
        while(i--) {

                new_array.push(old_array[i]);
                console.log(new_array);



        }}
        if($scope.AttSearch=="CustomerName"){
        while(i--) {
            if(old_array[i] && old_array[i]["CustomerName"] ==  $scope.cst_name) {
                new_array.push(old_array[i]);
                console.log(new_array);

            }

        }}
        if($scope.AttSearch=="ContactName"){
        while(i--) {
            if(old_array[i] && old_array[i]["ContactName"] ==  $scope.cst_name) {
                new_array.push(old_array[i]);
                console.log(new_array);

            }

        }}
        if($scope.AttSearch=="Country"){
        while(i--) {
            if(old_array[i] && old_array[i]["Country"] ==  $scope.cst_name) {
                new_array.push(old_array[i]);
                console.log(new_array);

            }

        }}
        $scope.searchResults=new_array;};



$scope.sendCustID = function(CustomerID) {messageService.publish("Customer",CustomerID.rowdata.CustomerID); 
    console.log(CustomerID.rowdata.CustomerID);

};


