// this line should not be removed
var example7Widget2 = angular.module( "wExample7Widget2",[]);

example7Widget2.controller( "wExample7Widget2Controller",['$scope',"messageService", function( $scope,messageService ) {


    messageService.subscribe("Customer",function(sender,CustomerID) {

	    var dq = new DataQuery( 'qCustomerInfoEx7' );
        dq.setParameters({"CustomerID":CustomerID});
        dq.setReqBody({
            "database": "Customers",
            "collection": "customers"
        });

        dq.execute().
        done( function() {
                $scope.$apply(function() {
                        $scope.Customer = dq.getData().data;
                        console.log(dq.getData());
                });
        })
        .fail(function(objError){
            console.log('get failed');
            console.log(objError.textError);
        });

        var dq1 = new DataQuery( 'qGetOrdersEx7' );
        dq1.setParameters({"CustomerID":CustomerID});
        dq1.setReqBody({
            "database": "Customers",
            "collection": "orders"
        });

        dq1.execute().
        done( function() {
                $scope.$apply(function() {
                        $scope.Order = dq1.getData();
                        console.log(dq1.getData());
                });
        })
        .fail(function(objError){
            console.log('get failed');
            console.log(objError.textError);
        });




	});
	$scope.sendOrderID = function(OrderID) {messageService.publish("Order",OrderID.rowdata.OrderID); 
    console.log("Here " + OrderID.rowdata.OrderID);};
}]);
