var wExample7Widget3 = angular.module("wExample7Widget3",[]);

wExample7Widget3.controller( "wExample7Widget3Controller",['$scope',"messageService", function( $scope,messageService ) {


    messageService.subscribe("Order",function(sender,OrderID) {

	    var dq = new DataQuery( 'qGetOrderDetailsEx7' );
        dq.setParameters({"OrderID":OrderID});
        dq.execute().
        done( function() {
          var tabs = dq.getData();
        $scope.$apply(function() {
        $scope.tabs = tabs;
        console.log($scope.tabs);
        });
        })
        .fail(function(objError){
            console.log('get failed');
            console.log(objError.textError);
        });

        $scope.ProductInfo = function(ProductID) {
            console.log(ProductID);
            var dq1 = new DataQuery( 'qProductInfo' );
            dq1.setParameters({"ProductID":ProductID});
dq1.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.ProdInfo = dq1.getData().data;
			console.log($scope.ProdInfo);
		});
	});

            };

	});
}]);
