// this line should not be removed
var wDfxDb = angular.module( "wExternalDb",[]);

wDfxDb.controller( 'wExternalDbController', function( $scope ) {
    $scope.removeDb = function(arg) {
        var dq = new DataQuery( 'qExternalDbRemove' );

	    dq.setParameters({"name":"France77"}); 
    	dq.executePost().
    	done( function() {
    		$scope.$apply(function() {
    			console.log('delete done');
    		});
    	})
    	.fail(function(objError){
    	    console.log('delete failed');
            console.log(objError.textError);
        });
    };

    $scope.updateDb = function(arg) {
        var dq = new DataQuery( 'qExternalDbUpdate' );

	    dq.setParameters({"name":"France77"}); 
    	dq.setReqBody({
            "name": "France77",
            "flag": "def"
        });

        dq.executePost().
    	done( function() {
    		$scope.$apply(function() {
    			console.log('update done');
    		});
    	})
    	.fail(function(objError){
    	    console.log('update failed');
            console.log(objError.textError);
        });
    };

    $scope.insertToDb = function(arg) {
        var dq = new DataQuery( 'qExternalDbInsert' );

    	dq.setReqBody({
            "name": "France77",
            "flag": "abc"
        });

        dq.executePost().
    	done( function() {
    		$scope.$apply(function() {
    			console.log('insert done');
    		});
    	})
    	.fail(function(objError){
    	    console.log('insert failed');
            console.log(objError.textError);
        });
    };

    $scope.getFromDb = function(arg) {
        var dq = new DataQuery( 'qExternalDb' );

    	dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			console.log(dq.getData());
    		});
    	})
    	.fail(function(objError){
    	    console.log('get failed');
            console.log(objError.textError);
        });
    };
});
