// this line should not be removed
var wFactoryExmaple = angular.module("wFactoryExmaple",[]);

//controller Logic
wFactoryExmaple.controller( "wFactoryExmapleController", function( $scope,  myNotifier ) {
	 myNotifier.notify_success('Welcome to DreamFace!');
	 myNotifier.notify_error('Check Server Capacity!');
	 $scope.alert_toastr = function(arg){
	     myNotifier.notify_success(arg)
	 };
});

//Defining Toastr Service as a factory
wFactoryExmaple.factory('myNotifier',function(myToastr){
    return{
       notify_success : function(msg){
           myToastr.success(msg);
       },
       notify_error : function(msg){
           myToastr.error(msg);
       }
    }
});
wFactoryExmaple.value('myToastr',toastr);
