// this line should not be removed
var wGetNews = angular.module("wGetNews",[]);

wGetNews.controller( "wGetNewsController", function( $scope ) {
    var dq = new DataQuery( 'qNYTimes' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.news = dq.getData();
    		});
    	});


});
