// this line should not be removed
var wGoogle = angular.module("wGoogle",[]);

wGoogle.controller( "wGoogleController", function( $scope ) {
    //initialize latitude/long and other variables
    $scope.lat=40.711667  ;
    $scope.lng=-74.0125;
    var geocoder;
    var map;

    //defining that marker and map position function
    $scope.markers=function(){
          map.setCenter($scope.results.geometry.location);
          var marker = new google.maps.Marker({
              map: map,
              position: $scope.results.geometry.location
          });
    };	

    //defining initialize function that will display the lat/lon on the map
    $scope.initialize=function initialize() {
        geocoder = new google.maps.Geocoder();
        var latlng = new google.maps.LatLng($scope.lat, $scope.lng);
        var mapOptions = {
            zoom: 15,
            center: latlng
        };
        map = new google.maps.Map(document.getElementById('map-canvas'), mapOptions);
    };

    //definig the getLatLong function that will get the lat/long and call the two previous functions
    $scope.getLatLong = function(arg) {	var dq = new DataQuery( 'qLatLong' );
        dq.setParameters({"address":$scope.address_mdl});
    	dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.results = dq.getData().results[0];
    			$scope.lat=dq.getData().results[0].geometry.location.lat;
    			$scope.lng=dq.getData().results[0].geometry.location.lng;
    			console.log($scope.results,$scope.lat,$scope.lng);
    			$scope.markers();
    		});
    	});
    	$scope.initialize();
    };

    //definig the start function that will start the intial map while loading the page
    $scope.start=function start() {
        google.maps.event.addDomListener(window, 'load', $scope.initialize);
    };
    $scope.start();

});

