// this line should not be removed
var wGrid = angular.module("wGrid",[]);

wGrid.controller( "wGridController", function( $scope ) {
    var dq = new DataQuery( 'qGetUsers' );
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData().data;
		});
	});


});
wGrid.directive('hello',function(){
    return {
      restrict:'C',
      template: '<button type="button" class="btn btn-info">Directive</button>'
    };
  });
