// this line should not be removed
var wGridExample = angular.module("wGridExample",[]);

wGridExample.controller( "wGridExampleController", function( $scope ) {
    var dq = new DataQuery( 'qGetUsers' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.results = dq.getData().data;
    		});
    	});

$scope.filterByOccupation = function() {
    console.log($scope.occupation_filter);
    var dq = new DataQuery( 'qGetUsers' );
    dq.setParameters({"occupation":$scope.occupation_filter});
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.results = dq.getData().data;
    		});
    	});

         };

});
