// this line should not be removed
var GridFilters = angular.module( "wGridFilter",[]);

GridFilters.controller( "wGridFilterController", function( $scope ) {
    //Defining Reset Button
    $scope.reset=function(){
        $scope.HotelFilter.rating='';
    };
    //Calling Grid Hotel Data
    var dq = new DataQuery( 'qHotelsQuery' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.results = dq.getData().data;
    		});
    	});

});
