// this line should not be removed
var GridFilters = angular.module( "wGridFilters",[]);

GridFilters.controller( "wGridFiltersController", function( $scope ) {
    var dq = new DataQuery( 'qGetcUsers' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.results = dq.getData().data;
    		});
    	});

});
