// this line should not be removed
var wGridImages = angular.module("wGridImages",[]);

wGridImages.controller( "wGridImagesController", function( $scope ) {
    var dq = new DataQuery( 'qPriorityTasks' );
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.tasks = dq.getData();
		});
	});
	$scope.showPriority = function(arg) { alert("Priority : "+arg.rowdata.TaskPriority); };
	$scope.showDetails = function(arg) { alert(arg.rowdata.synopsis)};
	$scope.showHelp = function(arg)  {
    $.SmartMessageBox({
        title : "Adding Image to Grid with Links from URL",
        content : "if you click on the cell, you will notice property type, If you choose image, you will bind the cell to image property from data that has the link in the form 'poster' : 'http://www.website.com/image.jpg' the grid will take the link from poster property and get the image to display in the cell. you can also add a callback function and pass row data to it. To lear more click <a href=''>Here</a>",
        buttons : '[Ok]'
    });
};


});
