// this line should not be removed
var wGridImagesUrl = angular.module("wGridImagesUrl",[]);

wGridImagesUrl.controller( "wGridImagesUrlController", function( $scope ) {
    //Calling the Queries for the Grid
    var dq = new DataQuery( 'qUsBoxOffice' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.results = dq.getData().movies;
    		});
	});
 var dq1 = new DataQuery( 'qPriorityTasks' );
    dq1.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.tasks = dq1.getData();
    		});
	});
	//Defining the notification alert functions.
	$scope.showPriority = function(arg) { alert("Priority : "+arg.rowdata.TaskPriority); };
	$scope.showDetails = function(arg) { alert(arg.rowdata.synopsis)};

});
