// this line should not be removed
var wGridMoviesHtml = angular.module("wGridMoviesHtml",[]);

wGridMoviesHtml.controller( "wGridMoviesHtmlController", ['$scope', '$sce', function($scope, $sce) {
    //Defining Data Query
	var dq = new DataQuery( 'qUsBoxOfficeSlice' );
	dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData();
		});
	});
	$scope.gridDataLoaded = function(data, dt) {
         $('span.bar').sparkline('html', { type: 'bar' });
    };

}]);
