// this line should not be removed
var wInputMasks = angular.module("wInputMasks",[]);

wInputMasks.controller( "wInputMasksController", function( $scope ) {
	$scope.showHelp = function(arg) {
        $.SmartMessageBox({
            title : "What is input mask ?",
            content : "An input mask can be used to constrain the input the user can give, this is constrained at the moment the user enters data into the system. So if the input mask is '9999', the user is not allowed to type in letters or special characters; only numbers will be accepted. To add input mask for input field, in the properties, type in the input mask in the mask field. use 9 for any number,a for any letter, * for any character Or you the exact character that you want to match ",
            buttons : '[Ok]'
        });
};

});
