// this line should not be removed
var wJobSearch = angular.module("wJobSearch",[]);

wJobSearch.controller( "wJobSearchController", function( $scope ) {
	var dq = new DataQuery( 'qGetCountries' );
    dq.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.countries = dq.getData();
    		});
	});

	var dq1 = new DataQuery( 'qUsaStates' );
    dq1.execute().
    	done( function() {
    		$scope.$apply(function() {
    			$scope.states = dq1.getData();
    		});
	});

	$scope.JobSearch = function(arg) { 
        var dq = new DataQuery( 'qJobSearchDice' );
        dq.setParameters({"country":$scope.country_mdl,"state":$scope.state_mdl,"text":$scope.keyword_mdl,"direct":$scope.direct_mdl,"skill":$scope.skill_mdl,"age":$scope.age_mdl,"sd":$scope.sort_mdl});
        dq.execute().
        	done( function() {
        		$scope.$apply(function() {
        			$scope.results = dq.getData().resultItemList;
        		});
	});

	};
	$scope.OpenInNewTab =function (ard) {
      var win = window.open(arg.rowdata.detailUrl, '_blank');
      win.focus();
};


});
