// this line should not be removed
var wMessages = angular.module("wMessages",[]);

wMessages.controller( "wMessagesController", function( $scope ) {

	$scope.vContent = "<i class='fa fa-clock-o'></i><b>&nbsp;&nbsp;&nbsp;Lorem ipsum dolor sit amet,</b> test <i>consectetur</i> adipisicing elit";
	$scope.vTitle = "My Alert Title";
	$scope.vLargeIcon = "fa fa-warning";
	$scope.vSmallIcon = "fa fa-bell";
	$scope.vColor = "#C46A69";
	$scope.vAnimation = "bounce animated";
	$scope.vAlert = 4;
	$scope.vTimeOut = 4000;


	$scope.vColorList = [
	    {label: "lightblue"},
	    {label: "lightyellow"},
	    {label: "lightred"},
	    {label: "lightgrey"},
	    {label: "#C46A69"},
	    {label: "#739E73"},
	    {label: "#5384AF"},
	    {label: "#296191"}
	    ];

	$scope.vIconsList = [
	    {label: "fa fa-warning"},
	    {label: "fa fa-warning fa-2x"},
	    {label: "fa fa-warning fa-4x"},
	    {label: "fa fa-bell"},
	    {label: "fa fa-shield"},
	    {label: "fa fa-check"},
	    {label: "fa fa-thumbs-up"},
	    {label: "fa fa-thumbs-down"}
	    ];	

	$scope.vAnimationList = [
	    {label: "bounce animated"},
	    {label: "flash animated"},
	    {label: "swing animated"},
	    {label: "rubberBand animated"},
	    {label: "fadeInRight animated"},
	    {label: "bounceInRight animated"},
	    {label: "fadeInDownBig animated"},
	    {label: "flip animated"},
	    {label: "rotate animated"}
	    ];		


    $scope.fancyAlertSmall = function() {

        $.smallBox({
                title: $scope.vTitle,
                content: $scope.vContent,
                color: $scope.vColor,
                icon: $scope.vLargeIcon + " " + $scope.vAnimation,
                iconSmall: $scope.vSmallIcon + " " + $scope.vAnimation,
                timeout: $scope.vTimeOut,
                number: $scope.vAlert
            });

        $scope.vSmallButtonClass = "btn-primary " + $scope.vSmallIcon;    
        return'';
        };

    $scope.fancyAlertBig = function() {
        $.bigBox({
                title: $scope.vTitle,
                content: $scope.vContent,
                color: $scope.vColor,
                icon: $scope.vLargeIcon + " " + $scope.vAnimation,
                iconSmall: $scope.vSmallIcon + " " + $scope.vAnimation,
                timeout: $scope.vTimeOut,
                number: $scope.vAlert
            });

        $scope.vLargeButtonClass = "btn-primary " + $scope.vLargeIcon;    
        return'';
        };


        $scope.smartModEg1 =  function () {
            $.SmartMessageBox({
                title: $scope.vTitle,
                content: $scope.vContent,
                buttons: '[No][Yes]'
            }, function (ButtonPressed) {
                if (ButtonPressed === "Yes") {
                    $.smallBox({
                        title: "Callback function",
                        content: "<i class='fa fa-clock-o'></i> <i>You pressed Yes...</i>",
                        color: "#659265",
                        iconSmall: "fa fa-check fa-2x fadeInRight animated",
                        timeout: 4000
                    });
                }
                if (ButtonPressed === "No") {
                    $.smallBox({
                        title: "Callback function",
                        content: "<i class='fa fa-clock-o'></i> <i>You pressed No...</i>",
                        color: "#C46A69",
                        iconSmall: "fa fa-times fa-2x fadeInRight animated",
                        timeout: 4000
                    });
                }
                $scope.$apply(function() {
                    $scope.mbModal = "you just chose " + ButtonPressed;
                    });
            });
        };


        $scope.smartModEg2 =  function () {
            $.SmartMessageBox({
                title: "Smart Alert: Select",
                content: "You can even create a group of options.",
                buttons: "[Done]",
                input: "select",
                options: "[Costa Rica][United States][Autralia][Spain]"
            }, function (ButtonPressed, Value) {
                $scope.$apply(function() {
                    $scope.mbModal = "you pressed " + ButtonPressed + " and chose " + Value;
                    });
            });

        };

        $scope.smartModEg3 =  function () {
            $.SmartMessageBox({
                title: "Enter Your Name",
                content: "<i class='fa fa-clock-o'></i> <i>You can enter <b>any</b> text or html...</i>",
                buttons: "[text!][any?][be][can][this]",
                input: "text",
                placeholder: "enter your name..."
            }, function (ButtonPressed, Value) {
                $scope.$apply(function() {
                    $scope.mbModal = "you pressed " + ButtonPressed + " and entered " + Value;
                    });
            });

        };


});
