// this line should not be removed
var wMiniCrud = angular.module("wMiniCrud",[]);

wMiniCrud.controller( "wMiniCrudController", function( $scope ) {

	$scope.crud_get = function(arg) { var dq = new DataQuery( 'qMiniCrudGet' );
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData().data;
		});
	});
	};
	$scope.crud_get();
	$scope.crud_rem = function(arg) {
	    var dq = new DataQuery( 'qCrudMiniRem' );
	    dq.setParameters({"id":arg.rowdata.id});
dq.execute().
	done( function() {
		$scope.$apply(function() {
        $scope.crud_get();
		});
	});

	};
	$scope.crud_upd = function(arg) { 
	    $scope.hide_add();
	    $scope.first_upd_mdl=arg.rowdata.firstName;
	    $scope.last_upd_mdl=arg.rowdata.lastName;
	    $scope.occupation_upd_mdl=arg.rowdata.occupation;
	    $scope.id_upd_mdl=arg.rowdata.id;

	    $scope.$apply(function() {
	    $scope.show_update='yes';})
	};
	$scope.hide_upd = function(arg) { $scope.show_update='no'; };
	$scope.crud_update=function(){
	    var dq = new DataQuery( 'qCrudMiniUpd' );
	    dq.setParameters({"id":$scope.id_upd_mdl});
	    dq.setReqBody({
    "id": $scope.id_upd_mdl,
    "firstName":  $scope.first_upd_mdl,
    "lastName":  $scope.last_upd_mdl,
    "occupation": $scope.occupation_upd_mdl
});
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.crud_get();
			$scope.show_update='no';
		});
	});

	};
	$scope.crud_add = function(arg) { 
	    	$scope.hide_upd();

	    $scope.show_add='yes';
	};
	$scope.hide_add = function(arg) { $scope.show_add='no'; };
	$scope.crud_insert = function(arg) { 
	    var dq = new DataQuery( 'qCrudMiniAdd' );
	    dq.setReqBody({
    "id": $scope.id_add_mdl,
    "firstName": $scope.first_add_mdl,
    "lastName": $scope.last_add_mdl,
    "occupation": $scope.occupation_add_mdl
});
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.crud_get();
			$scope.show_add='no';
		});
	});

	    };



});
