src_script:
// this line should not be removed
var wModernLayout2 = angular.module("wModernLayout2",[]);

wModernLayout2.controller( "wModernLayout2Controller", function( $scope ) {

    //------- Fake data to support image scrolling
    $scope.myAvatars = [
        {"photo":"/resources/Examples/images/eugene_lee.jpg"}, {"photo":"/resources/Examples/images/gary_donovan.jpg"},{"photo":"/resources/Examples/images/kathleen_byrne.jpg"},
        {"photo":"/resources/Examples/images/james_king.jpg"}, {"photo":"/resources/Examples/images/steven_wells.jpg"},{"photo":"/resources/Examples/images/julie_taylor.jpg"}
        ];

    //---------------------- This code is executed the first time the widget is created --------------------------
    //Display first item in the array
    $scope.currentIndex = 0
    $scope.selAvatar = $scope.myAvatars[$scope.currentIndex];    

    //Call these functions when Widget is created
    addAnimation('.myClass1', 'pulse');
    addAnimation('.myClass2', 'wobble');
    addAnimation('.myClass3', 'shake');
    addAnimation('.myClass4', 'swing');
    addAnimation('.myClass5', 'pulse');
    //---------------------- End of init code --------------------------


    //-------------- Change the index of the "card" to be shown" and apply it to the "model structure" used by the GC
    $scope.navigate = function(arg) {

        $scope.currentIndex = $scope.currentIndex + arg;

        if ($scope.currentIndex < 0) {
            $scope.currentIndex = $scope.myAvatars.length - 1;
        } else if ($scope.currentIndex >= $scope.myAvatars.length) {
            $scope.currentIndex = 0 ;
        }

        $scope.selAvatar = $scope.myAvatars[$scope.currentIndex]; 

        return '';
    }; 


});

//add Animation to the object holding the class name defined in the parameter className. These objects have already the class "animated" defined for them. animationStyle can be a vast variety of options
// consult http://daneden.github.io/animate.css/ to see all the animation styles available
addAnimation = function(className, animationStyle) {
    $(className).mouseenter( function() {
        $(className).addClass(animationStyle);
    });

    $(className).mouseout( function() {
        $(className).removeClass(animationStyle);
    });

    return '';
};

