// this line should not be removed
var wCharts_new = angular.module( "wNewCharts",[]);

wCharts_new.controller( 'wNewChartsController', function( $scope ) {
	var dq = new DataQuery( 'qDfxChartsExternal' );
    dq.execute().
        done( function() {
            $scope.$apply(function() {
                $scope.data = {};

                $scope.data.series = dq.getData().series;
                $scope.data.data = dq.getData().data;
            })
        });

    $scope.chartClick = function(data) { console.log(data); };

    $scope.changeChartData = function(arg) { 
        $scope.data.data = [
            {
                "x": "Laptops",
                "y": [
                    50,
                    250,
                    0
                ],
                "tooltip": "this is tooltip"
            },
            {
                "x": "Desktops",
                "y": [
                    100,
                    100,
                    100
                ]
            },
            {
                "x": "Mobiles",
                "y": [
                    200,
                    400,
                    500
                ]
            },
            {
                "x": "Tablets",
                "y": [
                    75,
                    0,
                    300
                ]
            }
        ];
    };
});
