// this line should not be removed
var wNewCheckboxes = angular.module("wNewCheckboxes",[]);

wNewCheckboxes.controller( "wNewCheckboxesController", function( $scope ) {
	$scope.fruits=[
        {'label':'apple','value':'Apple'},
        {'label':'orange','value':'Orange'},
        {'label':'pear','value':'Pear'}
    ];
    $scope.fruit = 'apple'; //for radio and toggle
    $scope.allFruits = ['apple','orange','pear']; // not use in model
    $scope.selectedFruits = [];// for simple checkbox model

    // function for simple checkbox
    $scope.changeMyList = function(){ // onchange
        console.log('--->>>', $scope.selectedFruits)
    };

    $scope.checkAll = function() {
        $scope.selectedFruits = ['Apple','Orange','Pear'];
    };
    $scope.uncheckAll = function() {
        $scope.selectedFruits = [];
    };
    $scope.checkOne = function() {
         $scope.selectedFruits = ['Orange'];
    };
});
